        if let dateCreate = data?.date {
            guard let date =
                    DateFormatter.utcFullISO8610Format.date(from: dateCreate) else {
                self.time.text = ""
                return
            }
            let formattedDate = DateFormatter.defaultFormatter.string(from: date)
            self.time.text = formattedDate
        }
